        X[i] = r * cos(theta);
        Y[i] = r * sin(theta);
        // draw_circle(X[i], Y[i], 2);
    }
}

void connect(int i, int j)
{
    i = i % N;
    j = j % N;
    draw_line(X[i], Y[i], X[j], Y[j]);
