int main()
{
    // initialize the random number generator
    srand(time(NULL));

    set_stroke_width(0.5);
    set_stroke("#44444480");

    make_circle(250);

    // connect_all(16);
    // connect_all(12);
    // connect_all(7);

    for (int i = 0; i &lt; N; i++)
    {
        int d = N / 6;

        connect(i, (i * i) % d);
    }

    save_sketch("lesson2.svg");
